<?phpfunction the_crumbs() {		if (is_home()) {			echo '<div class="breadcrumb">您现在位置：<a href="http://www.fei580.com/">飞瀛首页</a> &gt; <a href="http://www.fei580.com/international.aspx">国际机票查询</a> &gt; ';			echo '<a href="';			echo bloginfo('siteurl');			echo '">';			echo bloginfo('name');			echo '</a>';		}		if (!is_home()) {			echo '<div class="breadcrumb">您现在位置：<a href="http://www.fei580.com/">飞瀛首页</a> &gt; <a href="http://www.fei580.com/international.aspx">国际机票查询</a>';		}		if (is_category()) {			echo ' &gt; ';			echo get_category_parents( get_query_var('cat') , true , '' );		}		if (is_single()) {			echo ' &gt; ';			echo ' 机票详情 ';		}		if (is_page()) {			echo ' &gt; ';			echo the_title();		}	elseif (is_tag()) {echo ' &gt; ';single_tag_title();echo ' &gt; 机票 ';}	elseif (is_tax()) {echo ' &gt; ';setTitle();echo ' &gt; 机票 ';}	elseif (is_day()) {echo ' &gt; ';echo""; the_time('Y年m月d日'); echo'的机票';}	elseif (is_month()) {echo ' &gt; ';echo""; the_time('Y年m月'); echo'的机票';}	elseif (is_year()) {echo ' &gt; ';echo""; the_time('Y年'); echo'的机票';}	elseif (is_author()) {echo ' &gt; ';echo wp_title( ''); echo'';}	elseif (is_search()) {echo ' &gt; ';echo"搜索结果"; echo'';}	elseif (is_404()) {echo ' &gt; ';echo"亲，你迷路了！"; echo'';}	echo '</div>';}?>